mapper.select:
SELECT u.ID, u.USER_NAME, u.PASSWORD, u.ACTIVATED, u.DIRECT_LOGIN_KEY, ur.ROLE_ID FROM AC_USER u LEFT JOIN AC_USER_ROLE_XREF ur ON u.ID = ur.USER_ID where 1=1
and u.STORE_ID = #{storeId}
and u.USER_NAME = #{name}
ORDER BY u.ID DESC

select * from (SELECT u.ID, u.USER_NAME, u.PASSWORD, u.ACTIVATED, u.DIRECT_LOGIN_KEY, ur.ROLE_ID FROM AC_USER u LEFT JOIN AC_USER_ROLE_XREF ur ON u.ID = ur.USER_ID where 1=1
and u.ID = #{userId}
union all SELECT u.ID, u.USER_NAME, u.PASSWORD, u.ACTIVATED, u.DIRECT_LOGIN_KEY, ur.ROLE_ID FROM AC_USER u LEFT JOIN AC_USER_ROLE_XREF ur ON u.ID = ur.USER_ID where 1=1
and u.STORE_ID = #{storeId}
and ur.ROLE_ID = #{roleId}
and u.USER_NAME = #{name} ) rs ORDER BY rs.ID DESC
