mapper.select:
SELECT robot.ID, robot.NAME, robot.CODE, type.ID as TYPE_ID, config.LOW_BATTERY_THRESHOLD, config.SUFFICIENT_BATTERY_THRESHOLD, ifnull(robot.DESCRIPTION,'') as DESCRIPTION, robot.BOX_ACTIVATED, robot.ROBOT_ID_FOR_ELEVATOR, robot.BUSY, scene.ID as SCENE_ID, scene.NAME as SCENE_NAME FROM AS_ROBOT robot LEFT JOIN AS_ROBOT_TYPE type ON robot.TYPE_ID = type.ID LEFT JOIN AS_ROBOT_CONFIG config ON robot.ID = config.ROBOT_ID LEFT JOIN SCENES_ROBOTS_RELATIONS relation ON relation.ROBOT_ID = robot.ID LEFT JOIN AS_SCENE scene ON scene.ID = relation.SCENE_ID WHERE 1=1
AND robot.NAME like CONCAT('%',#{name},'%')
AND type.ID = #{type}
AND scene.ID = #{sceneId}
AND scene.NAME like CONCAT('%',#{sceneName},'%')
ORDER BY ID DESC

SELECT robot.ID, robot.NAME, robot.CODE, robot.ROBOT_ID_FOR_ELEVATOR, config.LOW_BATTERY_THRESHOLD, config.SUFFICIENT_BATTERY_THRESHOLD, ifnull(robot.DESCRIPTION,'') as DESCRIPTION, robot.BOX_ACTIVATED, robot.BUSY, robot.STORE_ID as storeId, robot.ROBOT_ID_FOR_ELEVATOR, pwd.BOX_NUM as boxNum, pwd.PASSWORD as password, ps.id AS "endPointId", ps.point_name AS endPointName, ps.point_alias AS endPointAlias, ps.scene_name AS endSceneName, ps.map_name AS endMapName, ps.point_level AS endPointLevel, ps.th AS endTh, ps.x AS endX, ps.y AS endY, ps.map_point_type_id AS endMapPointTypeId FROM AS_ROBOT robot LEFT JOIN AS_ROBOT_TYPE type ON robot.TYPE_ID = type.ID LEFT JOIN AS_ROBOT_CONFIG config ON robot.ID = config.ROBOT_ID LEFT JOIN AS_ROBOT_PASSWORD pwd ON robot.ID = pwd.ROBOT_ID LEFT JOIN AS_ROBOT_CHARGER_MAP_POINT_XREF robotChargerMapPointXref ON robotChargerMapPointXref.ROBOT_ID = robot.ID LEFT JOIN A_MAP_POINT ps ON robotChargerMapPointXref.CHARGER_MAP_POINT_ID = ps.ID WHERE 1=1
AND robot.CODE = #{code}
AND robot.STORE_ID = #{storeId}
AND robot.ID = #{robotId}
