mapper.select:
select

from OR_ORDER o
where o.id = #{id};

select

from OR_ORDER o


        o.order_setting_id = #{orderSetting.id}


        AND o.robot_id = #{robot.id}


        AND o.start_station_id = #{startStation.id}


        AND o.shelf_id = #{shelf.id}


        AND o.scene_id = #{scene.id}


        AND o.status = #{status}

mapper.update:
update OR_ORDER SET
robot_id = null,
status = #{status}
where id = #{id}
